package Solutions;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

class Solution567Test {
    Solution567 s = new Solution567();


    @Test
    void checkInclusion() {
        String s1_1 = "ab", s2_1 = "eidbaooo";
        boolean expect1 = true, actual1 = s.checkInclusion(s1_1,s2_1);

        String s1_2 = "ab", s2_2 = "eidboaoo";
        boolean expect2 = false, actual2 = s.checkInclusion(s1_2,s2_2);

        String s1_3 = "abcdxabcde", s2_3 = "abcdeabcdx";
        boolean expect3 = true, actual3 = s.checkInclusion(s1_3,s2_3);

        String s1_4 = "";
        String s2_4 = "";
        boolean expect4 = true, actual4 = s.checkInclusion(s1_4,s2_4);
        assertAll(
                ()-> Assertions.assertEquals(expect1,actual1),
                ()-> Assertions.assertEquals(expect2,actual2),
                ()-> Assertions.assertEquals(expect3,actual3),
                ()-> Assertions.assertEquals(expect4,actual4)
        );
    }
}